item.Unidos:
$('#input-address-01-new').autocomplete({
    lookup: locations,
    triggerSelectOnValidInput: true,
    minChars: 0,
    onSelect: function (suggestion) {                    
        if (self.NewPickupLocation.Location.Address() != suggestion.data.Address) {
            self.NewPickupLocation.Location.Address(suggestion.data.Address);
        }
        $('#input-address-01-new').error(false, '');
    },
    onInvalidateSelection:function () {
        if($('#input-address-01-new').val() != self.NewPickupLocation.Location.Address() || $('#input-address-01-new').val() == "") {
            self.NewPickupLocation.Location.Address("");
            if ($('#input-address-01-new').val() == "") {
                $('#input-address-01-new').error(true, 'Required Field');
            } else {
                $('#input-address-01-new').error(true, 'Invalid city');
            }
        }
    },
});

event:{blur: ValidLocationAddress.bind($data,$element)}
self.ValidLocationAddress = function (element) {
    if($(element).val().trim() != self.NewPickupLocation.Location.Address() || $(element).val() == "") {
        self.NewPickupLocation.Location.Address("");
        if ($(element).val() == "") {
            $(element).error(true, 'Required Field');
        } else {
            $(element).error(true, 'Invalid city');
        }
    }
    else {
        $(element).error(false, '');
    }
};

